import React, { useState } from "react";
import { registerApi } from "../../apis/Apis";
import { toast } from "react-toastify";

const Register = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordStrength, setPasswordStrength] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };

  const changeLastName = (e) => {
    setLastName(e.target.value);
  };

  const changeEmail = (e) => {
    setEmail(e.target.value);
  };

  const changePassword = (e) => {
    const pwd = e.target.value;
    setPassword(pwd);
    evaluatePasswordStrength(pwd);
  };

  const changeConfirmPassword = (e) => {
    setConfirmPassword(e.target.value);
  };

  const evaluatePasswordStrength = (password) => {
    const lengthRequirement = /.{8,12}/;
    const uppercaseRequirement = /[A-Z]/;
    const lowercaseRequirement = /[a-z]/;
    const numberRequirement = /[0-9]/;
    const specialCharRequirement = /[!@#$%^&*]/;

    if (password.length < 8) {
      setPasswordError("Password must be at least 8 characters long.");
      setPasswordStrength("Weak");
      return;
    }

    if (
      lengthRequirement.test(password) &&
      uppercaseRequirement.test(password) &&
      lowercaseRequirement.test(password) &&
      numberRequirement.test(password) &&
      specialCharRequirement.test(password)
    ) {
      setPasswordStrength("Strong");
      setPasswordError("");
    } else if (
      lengthRequirement.test(password) &&
      (uppercaseRequirement.test(password) ||
        lowercaseRequirement.test(password)) &&
      (numberRequirement.test(password) || specialCharRequirement.test(password))
    ) {
      setPasswordStrength("Medium");
      setPasswordError("");
    } else {
      setPasswordStrength("Weak");
      setPasswordError(
        "Password must contain uppercase, lowercase, numbers, and special characters."
      );
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!email.includes("@")) {
      toast.error("Invalid email format.");
      return;
    }

    if (passwordStrength === "Weak") {
      toast.error("Password is too weak.");
      return;
    }

    if (password !== confirmPassword) {
      toast.error("Passwords do not match.");
      return;
    }

    const data = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password,
      confirmPassword: confirmPassword,
    };

    registerApi(data)
      .then((res) => {
        if (res.data.success === true) {
          toast.success(res.data.message);
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  // Determine the background color for each dash based on password strength
  const getDashColor = (dashNumber, strength) => {
    if (strength === "Strong") return "green";
    if (strength === "Medium") return dashNumber <= 2 ? "orange" : "lightgray";
    if (strength === "Weak") return dashNumber === 1 ? "red" : "lightgray";
    return "lightgray";
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div
        className="d-flex"
        style={{
          boxShadow: "0px 0px 10px 0px rgba(0,0,0,0.2)",
          padding: "20px",
          borderRadius: "10px",
        }}
      >
        {/* Left side with image */}
        <div className="w-50" style={{ marginRight: "20px" }}>
          <img
            src="/assets/images/Cover.jpg"
            alt="Cover Page"
            className="w-100 h-100"
          />
        </div>

        {/* Right side with form */}
        <div className="w-50">
          <h1 className="m-4">Create an Account</h1>
          <form className="w-75" onSubmit={handleSubmit}>
            <label>Firstname</label>
            <input
              onChange={changeFirstName}
              className="form-control mb-2"
              type="text"
              placeholder="Enter your firstname"
              value={firstName}
            />

            <label>Lastname</label>
            <input
              onChange={changeLastName}
              className="form-control mb-2"
              type="text"
              placeholder="Enter your lastname"
              value={lastName}
            />

            <label>Email</label>
            <input
              onChange={changeEmail}
              className="form-control mb-2"
              type="email"
              placeholder="Enter your email"
              value={email}
            />

            <label>Password</label>
            <input
              onChange={changePassword}
              className="form-control mb-2"
              type="password"
              placeholder="Enter your password"
              value={password}
            />
            {/* Password Strength Indicator Line with 3 Dashes */}
            <div className="d-flex justify-content-between mb-2">
              <div
                style={{
                  width: "30%",
                  height: "5px",
                  backgroundColor: getDashColor(1, passwordStrength),
                  transition: "background-color 0.5s",
                }}
              />
              <div
                style={{
                  width: "30%",
                  height: "5px",
                  backgroundColor: getDashColor(2, passwordStrength),
                  transition: "background-color 0.5s",
                }}
              />
              <div
                style={{
                  width: "30%",
                  height: "5px",
                  backgroundColor: getDashColor(3, passwordStrength),
                  transition: "background-color 0.5s",
                }}
              />
            </div>
            {passwordError && (
              <div style={{ color: "red" }}>
                <small>{passwordError}</small>
              </div>
            )}

            <label>Confirm Password</label>
            <input
              onChange={changeConfirmPassword}
              className="form-control mb-2"
              type="password"
              placeholder="Enter your confirm password"
              value={confirmPassword}
            />

            <button
              className="btn btn-secondary w-100"
              type="submit"
              style={{ backgroundColor: "rgba(97, 124, 181, 1)" }}
            >
              Signup
            </button>
            <p className="mt-4, ">
              Already have an account? <a href="/">Login</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
