import React, { useState } from "react";
import { registerApi } from "../../apis/Apis";
import { toast } from "react-toastify";

const Register = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordStrength, setPasswordStrength] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };

  const changeLastName = (e) => {
    setLastName(e.target.value);
  };

  const changeEmail = (e) => {
    setEmail(e.target.value);
  };

  const changePassword = (e) => {
    const pwd = e.target.value;
    setPassword(pwd);
    evaluatePasswordStrength(pwd);
  };

  const changeConfirmPassword = (e) => {
    setConfirmPassword(e.target.value);
  };

  const evaluatePasswordStrength = (password) => {
    const lengthRequirement = /.{8,12}/;
    const uppercaseRequirement = /[A-Z]/;
    const lowercaseRequirement = /[a-z]/;
    const numberRequirement = /[0-9]/;
    const specialCharRequirement = /[!@#$%^&*]/;

    if (password.length < 8) {
      setPasswordError("Password must be at least 8 characters long.");
      setPasswordStrength("Weak");
      return;
    }

    if (
      lengthRequirement.test(password) &&
      uppercaseRequirement.test(password) &&
      lowercaseRequirement.test(password) &&
      numberRequirement.test(password) &&
      specialCharRequirement.test(password)
    ) {
      setPasswordStrength("Strong");
      setPasswordError("");
    } else if (
      lengthRequirement.test(password) &&
      (uppercaseRequirement.test(password) ||
        lowercaseRequirement.test(password)) &&
      (numberRequirement.test(password) || specialCharRequirement.test(password))
    ) {
      setPasswordStrength("Medium");
      setPasswordError("");
    } else {
      setPasswordStrength("Weak");
      setPasswordError(
        "Password must contain uppercase, lowercase, numbers, and special characters."
      );
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!email.includes("@")) {
      toast.error("Invalid email format.");
      return;
    }

    if (passwordStrength === "Weak") {
      toast.error("Password is too weak.");
      return;
    }

    if (password !== confirmPassword) {
      toast.error("Passwords do not match.");
      return;
    }

    const data = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password,
      confirmPassword: confirmPassword,
    };

    registerApi(data)
      .then((res) => {
        if (res.data.success) {
          toast.success(res.data.message);
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.error(err);
        toast.error("Internal Server Error!");
      });
  };

  return (
    <>
      <style>{`
        .container-flex {
          display: flex;
          height: 100vh;
          width: 100%;
          align-items: stretch;
        }
        .left-side {
          width: 50%;
          background-image: url('/assets/images/Cover.jpg');
          background-size: cover;
          background-position: center;
        }
        .right-side {
          width: 50%;
          display: flex;
          justify-content: center;
          align-items: center;
          padding: 20px;
        }
        .register-form {
          width: 75%;
          max-width: 400px;
        }
        .form-control {
          width: 100%;
          padding: 8px;
          margin-bottom: 15px;
          border: 1px solid #ccc;
          border-radius: 4px;
        }
        .btn-primary {
          width: 100%;
          padding: 10px;
          background-color: #007bff;
          border: none;
          color: white;
          cursor: pointer;
          border-radius: 4px;
        }
        .btn-primary:hover {
          background-color: #0056b3;
        }
        .password-strength-indicator {
          background-color: #e0e0e0;
          border-radius: 5px;
          height: 10px;
          position: relative;
          margin-bottom: 15px;
        }
        .password-strength-fill {
          height: 100%;
          position: absolute;
          border-radius: 5px;
          transition: width 0.5s ease, background-color 0.5s ease;
        }
      `}</style>
      <div className="container-flex">
        <div className="left-side"></div>
        <div className="right-side">
          <div className="register-form">
            <h1>Create an Account</h1>
            <form onSubmit={handleSubmit}>
              <input type="text" className="form-control" placeholder="First name" value={firstName} onChange={changeFirstName} />
              <input type="text" className="form-control" placeholder="Last name" value={lastName} onChange={changeLastName} />
              <input type="email" className="form-control" placeholder="Email" value={email} onChange={changeEmail} />
              <input type="password" className="form-control" placeholder="Password" value={password} onChange={changePassword} />
              <div className="password-strength-indicator">
                <div className="password-strength-fill" style={{ width: setPasswordStrength(passwordStrength), backgroundColor: setPasswordStrength(passwordStrength) }}></div>
              </div>
              <input type="password" className="form-control" placeholder="Confirm Password" value={confirmPassword} onChange={changeConfirmPassword} />
              <button type="submit" className="btn btn-primary">Sign Up</button>
            </form>
            <p>Already have an account? <a href="/">Log in</a></p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
