import React, { useState } from "react";
import { registerApi } from "../../apis/Apis";
import { toast } from "react-toastify";

const Register = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };

  const changeLastName = (e) => {
    setLastName(e.target.value);
  };

  const changeEmail = (e) => {
    setEmail(e.target.value);
  };

  const changePassword = (e) => {
    setPassword(e.target.value);
    setPasswordError(""); // Reset password error when user changes input
  };

  const changeConfirmPassword = (e) => {
    setConfirmPassword(e.target.value);
    setPasswordError(""); // Reset password error when user changes input
  };

  // Function to validate password
  const validatePassword = () => {
    const passwordPolicy = {
      minLength: 8,
      maxLength: 12,
      complexity: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*]).{8,12}$/,
    };

    if (password !== confirmPassword) {
      return "Passwords do not match.";
    }

    if (password.length < passwordPolicy.minLength || password.length > passwordPolicy.maxLength) {
      return `Password must be between ${passwordPolicy.minLength} and ${passwordPolicy.maxLength} characters long.`;
    }

    if (!passwordPolicy.complexity.test(password)) {
      return "Password must include uppercase, lowercase, numbers, and special characters.";
    }

    return "";
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const validationError = validatePassword();
    if (validationError) {
      setPasswordError(validationError);
      return;
    }

    const data = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password,
      confirmPassword: confirmPassword,
    };

    registerApi(data)
      .then((res) => {
        if (res.data.success === true) {
          toast.success(res.data.message);
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div
        className="d-flex"
        style={{
          boxShadow: "0px 0px 10px 0px rgba(0,0,0,0.2)",
          padding: "20px",
          borderRadius: "10px",
        }}
      >
        <div className="w-50" style={{ marginRight: "20px" }}>
          <img
            src="/assets/images/Cover.jpg"
            alt="Cover Page"
            className="w-100 h-100"
          />
        </div>

        <div className="w-50">
          <h1 className="m-4">Create an Account</h1>
          <form className="w-75" onSubmit={handleSubmit}>
            <label>Firstname</label>
            <input
              onChange={changeFirstName}
              className="form-control mb-2"
              type="text"
              placeholder="Enter your firstname"
            />

            <label>Lastname</label>
            <input
              onChange={changeLastName}
              className="form-control mb-2"
              type="text"
              placeholder="Enter your lastname"
            />

            <label>Email</label>
            <input
              onChange={changeEmail}
              className="form-control mb-2"
              type="email"
              placeholder="Enter your email"
            />

            <label>Password</label>
            <input
              onChange={changePassword}
              className="form-control mb-2"
              type="password"
              placeholder="Enter your password"
            />
            {passwordError && <div className="text-danger">{passwordError}</div>}

            <label>Confirm Password</label>
            <input
              onChange={changeConfirmPassword}
              className="form-control mb-2"
              type="password"
              placeholder="Confirm your password"
            />
            {passwordError && <div className="text-danger">{passwordError}</div>}

            <button
              className="btn btn-secondary w-100"
              type="submit"
              style={{ backgroundColor: "rgba(97, 124, 181, 1)" }}
            >
              Signup
            </button>
            <p className="mt-4">
              Already have an account? <a href="/">Login</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
