import React, { useState } from "react";
import { registerApi } from "../../apis/Apis";
import { toast } from "react-toastify";

const Register = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordStrength, setPasswordStrength] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const changeFirstName = (e) => setFirstName(e.target.value);
  const changeLastName = (e) => setLastName(e.target.value);
  const changeEmail = (e) => setEmail(e.target.value);
  const changePassword = (e) => {
    const pwd = e.target.value;
    setPassword(pwd);
    evaluatePasswordStrength(pwd);
  };
  const changeConfirmPassword = (e) => setConfirmPassword(e.target.value);

  const evaluatePasswordStrength = (password) => {
    const lengthRequirement = /.{8,12}/;
    const uppercaseRequirement = /[A-Z]/;
    const lowercaseRequirement = /[a-z]/;
    const numberRequirement = /[0-9]/;
    const specialCharRequirement = /[!@#$%^&*]/;

    if (password.length < 8) {
      setPasswordError("Password must be at least 8 characters long.");
      setPasswordStrength("Weak");
      return;
    }
    if (lengthRequirement.test(password) && uppercaseRequirement.test(password) &&
        lowercaseRequirement.test(password) && numberRequirement.test(password) &&
        specialCharRequirement.test(password)) {
      setPasswordStrength("Strong");
      setPasswordError("");
    } else {
      setPasswordStrength("Medium");
      setPasswordError("Password must contain uppercase, lowercase, numbers, and special characters.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email.includes("@")) {
      toast.error("Invalid email format.");
      return;
    }
    if (passwordStrength === "Weak") {
      toast.error("Password is too weak.");
      return;
    }
    if (password !== confirmPassword) {
      toast.error("Passwords do not match.");
      return;
    }

    registerApi({ firstName, lastName, email, password, confirmPassword })
      .then((res) => {
        toast.success(res.data.message);
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h1>Create an Account</h1>
        <form onSubmit={handleSubmit}>
          <input onChange={changeFirstName} value={firstName} placeholder="First name" />
          <input onChange={changeLastName} value={lastName} placeholder="Last name" />
          <input onChange={changeEmail} value={email} type="email" placeholder="Email" />
          <input onChange={changePassword} value={password} type="password" placeholder="Password" />
          <div className="password-indicator" style={{ backgroundColor: getPasswordStrengthColor(passwordStrength) }} />
          <input onChange={changeConfirmPassword} value={confirmPassword} type="password" placeholder="Confirm password" />
          <button type="submit">Sign up</button>
          <p>Already have an account? <a href="/">Log in</a></p>
        </form>
      </div>
    </div>
  );
};

export default Register;
